mapper.select:
select * from user 


        and name = #{name}


        and pwd = #{pwd}


        and head_img = #{headImg}


        and power = #{power}


        and status = #{status}


        and del = #{del}

mapper.update:
update user
name = #{name}, pwd = #{pwd}, head_img = #{headImg}, power = #{power}, status = #{status}, del = #{del},
where id = #{id}
